package client

import (
	"bytes"
	"testing"

	"github.com/number571/go-peer/pkg/crypto/asymmetric"
	"github.com/number571/go-peer/pkg/crypto/random"
	"github.com/number571/go-peer/pkg/payload"
	testutils "github.com/number571/go-peer/test/_data"
)

const (
	tcMessageSize = (100 << 10)
)

var (
	// AES block size = 16 bytes
	tgMessages = []string{
		testutils.TcBody,
		testutils.TcLargeBody,
		"",
		"A",
		"AA",
		"AAA",
		"AAAA",
		"AAAAA",
		"AAAAAA",
		"AAAAAAA",
		"AAAAAAAA",
		"AAAAAAAAA",
		"AAAAAAAAAA",
		"AAAAAAAAAAA",
		"AAAAAAAAAAAA",
		"AAAAAAAAAAAAA",
		"AAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA",
		"AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA",
		"",
		"",
		"",
		"",
		"",
		random.NewStdPRNG().GetString(tcMessageSize/2 - 1306), // wihout hex and additional padding
	}
)

func TestEncrypt(t *testing.T) {
	client1 := testNewClient()
	client2 := testNewClient()

	pl := payload.NewPayload(uint64(testutils.TcHead), []byte(testutils.TcLargeBody))
	msg, err := client1.EncryptPayload(client2.GetPubKey(), pl)
	if err != nil {
		t.Error(err)
		return
	}

	_, decPl, err := client2.DecryptMessage(msg)
	if err != nil {
		t.Error(err)
		return
	}

	if !bytes.Equal([]byte(testutils.TcLargeBody), decPl.GetBody()) {
		t.Error("data not equal with decrypted data")
		return
	}
}

func TestMessageSize(t *testing.T) {
	client1 := testNewClient()
	sizes := make([]int, 0, len(tgMessages))

	for _, smsg := range tgMessages {
		pl := payload.NewPayload(uint64(testutils.TcHead), []byte(smsg))
		msg, err := client1.EncryptPayload(client1.GetPubKey(), pl)
		if err != nil {
			t.Error(err)
			return
		}
		sizes = append(sizes, len(msg.ToBytes()))
	}

	for i := 0; i < len(sizes)-1; i++ {
		if sizes[i] != sizes[i+1] {
			t.Errorf(
				"len bytes of different messages = id(%d, %d) not equals = size(%d, %d)",
				i, i+1,
				sizes[i], sizes[i+1],
			)
			return
		}
	}
}

func testNewClient() IClient {
	return NewClient(
		NewSettings(&SSettings{
			FWorkSize:    10,
			FMessageSize: tcMessageSize,
		}),
		asymmetric.LoadRSAPrivKey(testutils.TcPrivKey1024),
	)
}
